import { useMemo, useState } from 'react'
import { StyleSheet, Image, Text, View, TextInput, Button } from 'react-native'
import data from '../../constants/skins'
import axios from 'axios'

const POST_COOKIE = `https://auth.riotgames.com/api/v1/authorization`
const GET_PLAYER_INFO = `https://auth.riotgames.com/userinfo`
const GET_STORE = (puuid) => `https://pd.ap.a.pvp.net/store/v2/storefront/${puuid}`

const Login = ({ navigation }) => {
  const [username, onChangeUsername] = useState('')
  const [password, onChangePassword] = useState('')
  const [isLoading, setIsLoading] = useState(false)
  const [errorMessage, setErrorMessage] = useState('')
  const [store, setStore] = useState([])

  const onLogin = async () => {
    setErrorMessage('')
    setIsLoading(true)
    try {
      const { data } = await axios.post(`http://127.0.0.1:3000/login`, {
        username,
        password
      },
      {
        headers: {
          'Content-Type': 'application/json'
        }
      })
      
      if(data.success === true) {
        setStore(data.store)
      } else {
        setErrorMessage(data.message)
      }
      setIsLoading(false)
    } catch (error) {
      setErrorMessage(error.message)
      setIsLoading(false)
    }
  }

  const images = useMemo(() => {
    const list = [
      "https://media.valorant-api.com/weaponskinchromas/f9bde748-403a-acd2-e760-33b8ea9c1b80/fullrender.png",
      "https://media.valorant-api.com/weaponskinchromas/77b09a8e-45c4-47e2-449f-44bd6f3a2f7a/fullrender.png",
      "https://media.valorant-api.com/weaponskinchromas/6593259b-43f1-ab4a-5cb9-a0977fb3750f/fullrender.png",
      "https://media.valorant-api.com/weaponskinchromas/a74b5b26-4bf2-3d4c-4176-15806ebed55e/fullrender.png"
    ]
    if (store.length > 0) {
      store.map(item => {
        data.skins.map(({ displayName, fullRender }) => {
          if (item === displayName) {
            list.push(fullRender)
          }
        })
      })
    }

    return list
  }, [store])

  return (
    <View style={styles.container}>
      <TextInput 
        style={styles.input}
        placeholder='Username'
        onChangeText={onChangeUsername}
        value={username}
        nativeID='username'
      />
      <TextInput 
        style={styles.input}
        placeholder='Password'
        onChangeText={onChangePassword}
        value={password}
        nativeID='password'
        secureTextEntry
      />
      {store.length > 0 && store.map(item => <Text>{item}</Text>)}
      <View style={styles.imageContainer} >
        {images.length > 0 &&
          images.map(item => 
            <Image source={{ uri: item }} style={styles.image} key={item}/>
          )
        }
      </View>
      {errorMessage && <Text>{errorMessage}</Text>}
      <Button
        title='Login'
        onPress={onLogin}
        disabled={isLoading || !username || !password}
      />
    </View>
  )
}

const styles = StyleSheet.create({
  input: {
    padding: '5px',
    border: '1px solid #CCCCCC',
    borderRadius: '8px',
    margin: '5px',
  },
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  imageContainer: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '100%',
    backgroundColor: 'yellow',
    flexDirection: 'row',
  },
  image: {
    width: '100%',
    height: undefined,
    aspectRatio: 1,
}
})

export default Login
